import React from "react";
import 'bootstrap/dist/css/bootstrap.min.css';


const PromiseExampleHome = () => {
    const [sortField, setSortField] = useState('');
    const [order, setOrder] = useState('asc');
    const [data, setData] = useState({
        columns: ['Name', 'Age', 'Location'],
        rows: [
            { id: 1, Name: 'John', Age: 30, Location: 'New York' },
            { id: 2, Name: 'Alice', Age: 25, Location: 'Los Angeles' },
            { id: 3, Name: 'Bob', Age: 35, Location: 'Chicago' },
            { id: 4, Name: 'Emily', Age: 28, Location: 'Houston' },
        ],
    });

    const handleSort = (field) => {
        setSortField(field);
        setOrder(order === 'asc' ? 'desc' : 'asc');
    };

    const sortedData = data.rows.sort((a, b) => {
        const valueA = a[sortField];
        const valueB = b[sortField];

        if (order === 'asc') {
            return valueA < valueB ? -1 : 1;
        } else {
            return valueA > valueB ? -1 : 1;
        }
    });

    return (
        <table>
            <thead>
                <tr>
                    {data.columns.map((column) => (
                        <th key={column} onClick={() => handleSort(column)}>
                            {column}
                        </th>
                    ))}
                </tr>
            </thead>
            <tbody>
                {sortedData.map((row) => (
                    <tr key={row.id}>
                        {data.columns.map((column) => (
                            <td key={column}>{row[column]}</td>
                        ))}
                    </tr>
                ))}
            </tbody>
        </table>
    );

}
export default PromiseExampleHome;
