import React, { useEffect } from "react";

const CurdEditChanges = ({ show, handleClose }) => {
    useEffect(() => {
        if (show) {
            const modalElement = document.getElementById('myModal');
            const modal = new bootstrap.Modal(modalElement);
            modal.show();
        }
    }, [show]);

    return (
        <div id="myModal" className={`modal fade ${show ? 'show' : ''}`} tabIndex="-1" aria-labelledby="modalLabel" aria-hidden="true" style={{ display: show ? 'block' : 'none' }}>
            <div className="modal-dialog">
                <div className="modal-content">
                    <div className="modal-header">
                        <h4 className="modal-title" id="modalLabel">Modal Header</h4>
                        <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close" onClick={handleClose}></button>
                    </div>
                    <div className="modal-body">
                        <p>Some text in the modal.</p>
                    </div>
                    <div className="modal-footer">
                        <button type="button" className="btn btn-secondary" data-bs-dismiss="modal" onClick={handleClose}>Close</button>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default CurdEditChanges;
