import React, { useState } from 'react';
import axios from 'axios';

function UploadMultipleImage() {
    const [state, setState] = useState({
        multipleFiles: [],
    });

    const handleChange = (e) => {
        const files = Array.from(e.target.files);
        console.log("files", files);

        // Update state with selected files
        setState({
            multipleFiles: files,
        });

        // Create formData object to send files
        const formData = new FormData();
        files.forEach((file, index) => {
            formData.append(`file${index}`, file);
        });

        // Send files via Axios
        axios.post("https://fapi.rizee.in/file_upload", formData)
            .then(response => {
                console.log("response", response);
                // Handle success if needed
            })
            .catch(error => {
                console.error("Error:", error);
                // Handle error if needed
            });
    };

    return (
        <>
            <input type="file" multiple onChange={(e) => handleChange(e)} />
            <div>
                {state.multipleFiles.map((file, index) => (
                    <div key={index} style={{ marginBottom: '10px' }}>
                        <img
                            src={URL.createObjectURL(file)}
                            alt={file.name}
                            width={50}
                            height={50}
                            style={{ objectFit: 'cover', marginRight: '10px' }}
                        />
                        <span>{file.name}</span>
                    </div>
                ))}
            </div>
        </>
    );
}

export default UploadMultipleImage;
