import React, { useState } from "react";
import { Row, Col, Form, ListGroupItem } from "react-bootstrap";

const Example1 = () => {
    const allCities = [
        { id: 1, name: "India", cities: ["Hydrabad", "Mumbai"] },
        { id: 2, name: "USA", cities: ["New York", "Los Angeles"] },
        { id: 3, name: "USSA", cities: ["New City", "Los Angeles"] },
    ];

    const [state, setState] = useState({
        cities: allCities,
        selectCityName: "",
        cityName: "",
    });

    const handleChange = (e) => {
        const { name, value } = e.target;

    };

    const { cities } = state;

    return (
        <>
            <Row className="mt-3">
                <Col sm={6}>
                    <Form.Select size="sm" onChange={handleChange} value={state.selectCityName} name="SelectName">
                        <option>--Select Name--</option>
                        {
                            cities.map((item)=>{
                                <option value={item.id} key={item.id}>{item.name}</option>
                            })
                        }
                    </Form.Select>
                </Col>
                <Col sm={6}>
                    <Form.Select size="sm">
                        <option>--Select City--</option>
                    </Form.Select>
                </Col>
            </Row>
        </>
    )
}
export default Example1;
