import React, { useState } from "react";
import { Row, Col, Card, Placeholder } from "react-bootstrap";

const Example4 = () => {
    const [state, setState] = useState({
        firstName: "",
        lastName: "",
    });
    console.log("statestate",state);
    const formFields = [
        { type: "text", name: "firstName", Placeholder: "First Name", label: "First Name" },
        { type: "text", name: "LastName", Placeholder: "Last Name", label: "Last Name" },
    ];

    const handleChange = (e) => {
        const { name, value } = e.target
        setState(prevState => ({ ...prevState, [name]: value }));
    };

    return (
        <>
            <Row>
                <Col sm={6}>
                    {
                        formFields.map((item) => (
                            <>
                                <h6>{item.label}</h6>
                                <input type={item.type} placeholder={item.Placeholder} onChange={handleChange} name={item.name} value={state[item.name]}/>
                            </>
                        ))
                    }
                </Col>
            </Row>
        </>
    )
}
export default Example4;
