import React, { useState } from 'react';


const Types = [
    { type: "audio", extension: ["mp3", "flac", "wav", "ogg", "m3u", "acc", "wma", "midi", "aif", "m4a", "mpa", "pls", "aiff", "mid", "midi", "au", "snd", "mpga", "mp2", "bwf", "aax", "aa", "aac", "caf", "m4r", "ac3", "eac3", "ec3","mpeg"] },
    { type: "video", extension: ["mp3", "mp4", "mkv", "m4v", "mov", "mpg", "avi", "vfw", "mpg4", "mpe", "m75", "m15", "dif", "ts", "mov", "qt", "dif", "wmv"] },
    { type: "image", extension: ["jpg", "jpeg", "apng", "avif", "gif", "jfif", "pjpeg", "pjp", "png", "svg", "webp", "bmp", "ico", "cur", "tif", "tiff", "psd"] },
    { type: "document", extension: ["pdf", "png", "doc", "docx", "xls", "html", "htm", "txt", "rtf", "msg", "zip", "xlsx", "xlsm", "java", "c", "class", "cpp", "py", "php", "html", "htm", "cs", "css", "pp", "csv", "rar", "jsx"] }
];

function UploadMultipleImage() {
    const [arrayOfObjects, setArrayOfObjects] = useState([]);
    console.log("arrayOfObjects", arrayOfObjects);

    const onFileChange = async (event) => {
        event.preventDefault();
        const files = Array.from(event.target.files);
        try {
            const filesData = await Promise.all(files.map(file => new Promise((resolve, reject) => {
                const reader = new FileReader();
                reader.onload = () => resolve({ base64Data: reader.result, file });
                reader.onerror = reject;
                reader.readAsDataURL(file);
            })));
            setArrayOfObjects(filesData);
        } catch (error) {
            console.error("Error:", error);
        }
    };

        const imageStyles = {
        width: "100%",
        height: "250px",
        cursor: "pointer",
        gap: "10px",
        marginTop: "20px"
    };

    const scrollingStyle = {
        overflowY: "auto",
        maxHeight: "400px"
    };

    return (
        <>
            <input type="file" multiple onChange={onFileChange} />
            <div>
            {state.arrayOfObjects.map((item, index) => {
                                        const imageUrl = item;
                                        const fileExtension = item.split('.').pop().toLowerCase()
                                        const fileType = Types.find(type => type.extension.includes(fileExtension))
                                        return (
                                            <Col sm={6} key={index}>
                                                {fileType ? (
                                                    fileType.type === "image" ? (
                                                        <div>
                                                            <img
                                                                src={imageUrl}
                                                                alt="Preview"
                                                                style={imageStyles}
                                                            />
                                                        </div>
                                                    ) : fileType.type === "video" ? (
                                                        <video
                                                            src={imageUrl}
                                                            controls={true}
                                                            autoPlay={false}
                                                            alt="Preview"
                                                            style={imageStyles}
                                                        ></video>
                                                    ) : fileType.type === "document" ? (
                                                        <div>
                                                            <img src={NoPreview}
                                                                alt="Preview"
                                                                style={imageStyles}
                                                            />
                                                        </div>
                                                    ) : fileType.type === "audio" ? (
                                                        <div>
                                                           
                                                        </div>
                                                    ) : null
                                                ) : (
                                                    <div className="text-success">Unsupported file type</div>
                                                )}
                                             </Col>
                                        );
                                    })}
            </div>
        </>
    );
}

export default UploadMultipleImage;
