import React, { useState } from 'react';
import axios from 'axios';

function UploadMultipleImage() {
    const [state, setState] = useState({
        arrayOfObjects: [],
    });

    const onFileChange = (event) => {
        event.preventDefault();
        const files = event.target.files;
        const base64Strings = [];
        for (let i = 0; i < files.length; i++) {
            const file = files[i];
            const reader = new FileReader();
            const formData = new FormData();
            formData.append('file', file);
             reader.onload = () => {
                let base64String = reader.result;
                base64Strings.push(base64String);
                if (base64Strings.length == files.length) {
                    axios.post("https://fapi.rizee.in/file_upload", {
                        "file": base64Strings
                    }).then(response => {
                        console.log("responseresponse",response);
                        setState((prevState) => ({
                            ...prevState,
                            arrayOfObjects:[]
                        }));
                    }).catch(error => {
                        console.error("Error:", error);
                    });
                }
            };
            reader.readAsDataURL(file);
        }
    };

    return (
        <>
            <input type="file" multiple onChange={(e) => onFileChange(e)} />
            <div>
                {state.arrayOfObjects.map((obj, index) => (
                    <div key={index} style={{ marginBottom: '10px' }}>
                        <img
                            src={obj.base64Data}
                            alt={obj.file.name}
                            width={50}
                            height={50}
                            style={{ objectFit: 'cover', marginRight: '10px' }}
                        />
                        <span>{obj.file.name}</span>
                    </div>
                ))}
            </div>
        </>
    );
}

export default UploadMultipleImage;
