import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

const ExampleCard = () => {
  const [cardData] = useState([
    { id: 1, headerName: 'Naresh' },
    { id: 2, headerName: 'Suresh' },
    { id: 3, headerName: 'Ganesh' },
    { id: 4, headerName: 'Harish' },
  ]);

  const [selectOption,setSelectOption]=useState(1);
  const handleSelection =(option)=>{
    setSelectOption(option);
  };

  const renderOption =()=>{
    const selectedCard = cardData.find(card => card.id === selectedOption);

  }
  return (
    <div className="d-flex flex-wrap">
      {cardData.map((item) => (
        <div key={item.id} className="card m-2" style={{ width: '18rem' }}>
          <div className="card-body text-center">
            <h5 className="card-title">{item.headerName}</h5>
          </div>
        </div>
      ))}
    </div>
  );
};

export default ExampleCard;
