import React, { useEffect, useState } from "react";

const contacts = [
    {
        name: "John Doe",
        mobile: "1234567890",
        email: "john@example.com",
        gender: "male"
    },
    {
        name: "Jane Smith",
        mobile: "0987654321",
        email: "jane@example.com",
        gender: "female"
    },
    {
        name: "Michael Johnson",
        mobile: "3456789012",
        email: "michael@example.com",
        gender: "male"
    },
    {
        name: "Emily Davis",
        mobile: "5678901234",
        email: "emily@example.com",
        gender: "female"
    },
    {
        name: "David Brown",
        mobile: "7890123456",
        email: "david@example.com",
        gender: "male"
    }
];

const Example = () => {

    const [state, setState] = useState({
        columns: ['Sno', 'Name', 'Mobile Number', 'Email', 'Gender'],
        rowData: []
    });

    useEffect(() => {
        setState(prevState => ({ ...prevState, rowData: contacts }))
    }, []);

    return (
        <>
            <table className="table">
                <thead>
                    <tr>
                        {state.columns.map((column, index) => (
                            <th key={index} scope="col">{column}</th>
                        ))}
                    </tr>
                </thead>
                <tbody>
                    {state.rowData.map((row, indexOfFirstRecord) => (
                        <tr key={row.id}>
                            <td>{indexOfFirstRecord + 1}</td>
                            {state.columns.slice(1).map((column, index) => (
                                <td key={index}>{row[column.toLowerCase().replace(" ", "_")]}</td>
                            ))}
                        </tr>
                    ))}
                </tbody>
            </table>
        </>
    )
}

export default Example;
