import React, { useState } from 'react';
import axios from 'axios';

function UploadMultipleImage() {
    const [state, setState] = useState({
        arrayOfObjects: [],
    });

    const handleChange = (e) => {
        const files = Array.from(e.target.files);
        const filesWithPreview = [];

        files.forEach(file => {
            const reader = new FileReader();
            reader.onload = () => {
                const fileObj = {
                    file,
                    preview: reader.result,
                };
                filesWithPreview.push(fileObj);

                if (filesWithPreview.length === files.length) {
                    setState(prevState => ({
                        arrayOfObjects: [...prevState.arrayOfObjects, ...filesWithPreview],
                    }));

                    // Create formData object to send files
                    const formData = new FormData();
                    files.forEach((file, index) => {
                        formData.append(`file${index}`, file);
                    });

                    // Send files via Axios
                    axios.post("https://fapi.rizee.in/file_upload", formData)
                        .then(response => {
                            console.log("response", response);
                            // Handle success if needed
                        })
                        .catch(error => {
                            console.error("Error:", error);
                            // Handle error if needed
                        });
                }
            };
            reader.readAsDataURL(file);
        });
    };

    return (
        <>
            <input type="file" multiple onChange={(e) => handleChange(e)} />
            <div>
                {state.arrayOfObjects.map((obj, index) => (
                    <div key={index} style={{ marginBottom: '10px' }}>
                        <img
                            src={obj.preview}
                            alt={obj.file.name}
                            width={50}
                            height={50}
                            style={{ objectFit: 'cover', marginRight: '10px' }}
                        />
                        <span>{obj.file.name}</span>
                    </div>
                ))}
            </div>
        </>
    );
}

export default UploadMultipleImage;
