import React, { useState } from "react";
import './CurdOpration.css'
import CurdEditChanges from "./EditChanges";
import { Row, Col, Card, Form } from "react-bootstrap";

const CurdOperationHome = () => {
    const [state, setState] = useState({
        id: 0,
        userName: "",
        age: "",

        userNameError: "",
        ageError: "",

        UserData: [],
        updatedUserData: [],

        editModal: false,
    });

    console.log("CURD", state.UserData);

    const handleChange = (e) => {
        e.priventDefault();
        const { name, value } = e.target;
        setState(prevState => ({ ...prevState, [name]: value, userNameError: "", ageError: "" }));
    }

    const handleSubmit = () => {
        const { userName, age, id } = state;
        if (!userName) {
            setState(prevState => ({ ...prevState, userNameError: "Please Enter User Name" }));
            return;
        } else if (!age) {
            setState(prevState => ({ ...prevState, ageError: "Please Enter Age" }));
            return;
        }
        let newUser = {
            "id": id + 1,
            "userName": userName,
            "age": age
        }
        setState(prevState => ({ ...prevState, UserData: [...prevState.UserData, newUser], userName: "", age: "", id: id + 1 }));
    }

    const handleDelete = (index) => {
        const deleteUserData = state.UserData.filter((_, i) => i !== index);
        setState(prevState => ({ ...prevState, UserData: deleteUserData }));
    }

    const handleEdit = (row) => {
        setState(prevState => ({ ...prevState, updatedUserData: [row], editModal: true }))
    }

    const handleClose = () => {
        setState(prevState => ({ ...prevState, editModal: false }))
    }

    const handleUpdateContact = (newData) => {
        console.log("newData", newData);
        setState(prevState => {
            const updatedUserData = prevState.UserData.map(user => {
                if (user.id === newData.id) {
                    return { ...user, ...newData };
                }
                return user;
            });
            return { ...prevState, UserData: updatedUserData };
        });
    }


    return (
        <div>
            <div className="card">
                <div className="card-body text-dark card_bg_Color">
                    <h4 className="text-center">Naresh</h4>
                </div>
            </div>

            <Row>
                <Col sm={12}>
                    <Card className="creation_Card mt-2">
                        <Card.Body >
                            <Form>
                                <Col sm={12}>
                                    <Form.Label className="fw-bold">UserName <span>{state.userNameError}</span></Form.Label>
                                    <Form.Control type="text" name="userName" value={state.userName} className="form-control" onChange={handleChange} placeholder="Enter User Name" />
                                </Col>

                                <Col sm={12}>
                                    <Form.Label className="fw-bold">Age <span>{state.ageError}</span></Form.Label>
                                    <Form.Control type="number" name="age" value={state.age} className="form-control" onChange={handleChange} placeholder="Enter Age" />
                                </Col>

                                <Col sm={12}>
                                    <button className="btn btn-success mt-3" onClick={handleSubmit}>Submit</button>
                                </Col>
                            </Form>
                        </Card.Body>
                    </Card>
                </Col>
            </Row>

            <div className="mt-3">
                <table class="table table-bordered text-center">
                    <thead>
                        <tr>
                            <th scope="col">#</th>
                            <th scope="col">Name</th>
                            <th scope="col">Age</th>
                            <th scope="col">Edit</th>
                            <th scope="col">Delete</th>
                        </tr>
                    </thead>
                    <tbody>
                        {state.UserData.map((user, index) => (
                            <tr key={index}>
                                <td>{index + 1}</td>
                                <td>{user.userName}</td>
                                <td>{user.age}</td>
                                <td>
                                    <button className="btn btn-success" onClick={() => handleEdit(user)}>Edit</button>
                                </td>
                                <td>
                                    <button className="btn btn-danger" onClick={() => handleDelete(index)}>Delete</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
            {
                state.editModal && (
                    <CurdEditChanges
                        updatedUserData={state.updatedUserData}
                        show={state.editModal}
                        handleClose={handleClose}
                        handleUpdateContact={handleUpdateContact}
                    />
                )
            }
        </div>
    )
}
export default CurdOperationHome;
