import React, { useState } from "react";
import { Row, Col } from "react-bootstrap";

const Example4 = () => {
    const [state, setState] = useState({
        firstName: "",
        lastName: "",
        selectionOption: "0",
    });

    const formFields = [
        { type: "text", name: "firstName", placeholder: "First Name", label: "First Name" },
        { type: "select", name: "selectionOption", placeholder: "", label: "Select Option", options: [{ id: 1, name: "Naresh" }, { id: 2, name: "Suresh" }] },
        { type: "text", name: "lastName", placeholder: "Last Name", label: "Last Name" },
    ];

    const handleChange = (e) => {
        const { name, value } = e.target;
        setState(prevState => ({ ...prevState, [name]: value }));
    };

    return (
        <>
            <Row>
                <Col sm={6}>
                    {formFields.map((item, index) => (
                        <div key={index} className="mt-3">
                            <h6>{item.label}</h6>
                            {item.type === "text" ? (
                                <input
                                    type={item.type}
                                    placeholder={item.placeholder}
                                    name={item.name}
                                    value={state[item.name]}
                                    onChange={handleChange}
                                />
                            ) : item.type === "select" ? (
                                <select
                                    name={item.name}
                                    value={state[item.name]}
                                    onChange={handleChange}
                                >
                                    <option value="0">--Select Name--</option>
                                    {item.options.map(option => (
                                        <option key={option.id} value={option.id}>
                                            {option.name}
                                        </option>
                                    ))}
                                </select>
                            ) : null}
                        </div>
                    ))}
                </Col>
            </Row>
        </>
    );
};

export default Example4;
