    //             ]);
    //             setCurrentBatch(prevBatch => prevBatch + 1);
    //         }, 2000);
    //         return () => clearTimeout(timeout);
    //     }
    // }, [data, currentBatch]);

    return (
        <>
            {displayedData.map((item, index) => (
                <div key={index}>{index + 1}.{item.title}</div>
            ))}
        </>
    );
};

export default Example3;
