import React, { useState } from 'react';
import axios from 'axios';

function UploadMultipleImage() {
    const [arrayOfObjects, setArrayOfObjects] = useState([]);

    const onFileChange = async (event) => {
        event.preventDefault();
        const files = event.target.files;

        const fileReadPromises = Array.from(files).map(file => {
            return new Promise((resolve, reject) => {
                const reader = new FileReader();
                reader.onload = () => {
                    resolve({ base64Data: reader.result, file });
                };
                reader.onerror = reject;
                reader.readAsDataURL(file);
            });
        });

        try {
            const filesData = await Promise.all(fileReadPromises);
            const base64Strings = filesData.map(data => data.base64Data);

            await axios.post("https://fapi.rizee.in/file_upload", { "file": base64Strings });

            setArrayOfObjects(filesData);
        } catch (error) {
            console.error("Error:", error);
        }
    };

    return (
        <>
            <input type="file" multiple onChange={onFileChange} />
            <div>
                {arrayOfObjects.map((obj, index) => (
                    <div key={index} style={{ marginBottom: '10px' }}>
                        <img
                            src={obj.base64Data}
                            alt={obj.file.name}
                            width={50}
                            height={50}
                            style={{ objectFit: 'cover', marginRight: '10px' }}
                        />
                        <span>{obj.file.name}</span>
                    </div>
                ))}
            </div>
        </>
    );
}

export default UploadMultipleImage;
