import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

const ExampleCard = () => {
  const [cardData] = useState([
    { id: 1, headerName: 'Naresh' },
    { id: 2, headerName: 'Suresh' },
    { id: 3, headerName: 'Ganesh' },
    { id: 4, headerName: 'Harish' },
  ]);

  const [selectOption,setSelectOption]=useState(1);
  const handleSelection =(option)=>{
    setSelectOption(option);
  };

  const renderOption = () => {
    const selectedCard = cardData.find(card => card.id === selectedOption);
    if (selectedCard) {
      return (
        <div className="mt-4">
          <h4>Selected Card</h4>
          <p>ID: {selectedCard.id}</p>
          <p>Name: {selectedCard.headerName}</p>
        </div>
      );
    }
    return null;
  };

  return (
     <div className="container">
      <div className="d-flex flex-wrap">
        {cardData.map((item) => (
          <div
            key={item.id}
            className={`card m-2 ${selectedOption === item.id ? 'border-primary' : ''}`}
            style={{ width: '18rem', cursor: 'pointer' }}
            onClick={() => handleSelection(item.id)}
          >
            <div className="card-body text-center">
              <h5 className="card-title">{item.headerName}</h5>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ExampleCard;
