import React, { useState } from "react";

const arrayData = [{ id: 1, name: "Naresh" }, { id: 2, name: "Raresh" }, { id: 3, name: "Suresh" },]
const Example2 = () => {
    const [state, setState] = useState({
        arrayDetails: arrayData,
        selectedValue: "",
    });

    const handleInputChange = (e) => {
        setState(preVstate => ({ ...preVstate, selectedValue: e.target.value }))
    }

    const checkType = [{ type: "checkbox", label: "Checked", value: state.selectedValue }]
    return (
        <>
            <ul>
                {
                    state.arrayDetails.map((item, index) => {
                        debugger;
                        return (
                            <li className="h4">{checkType.map((checkbox, idx) => (
                                <input key={idx} type={checkbox.type} onChange={handleInputChange} />
                            ))} {index + 1} - {item.name}</li>
                        )
                    })
                }
            </ul>
        </>
    )
}
export default Example2;
