import React, { useState } from "react";
import { Modal, Row, Col } from 'react-bootstrap';
import './CurdOpration.css'


const CurdEditChanges = ({ handleClose, show, }) => {
    const [state, setState] = useState({
        userName: "",
        age: "",

        userNameError: "",
        ageError: "",

        UserData: [],
        updatedUserData: [],

        editModal: false,
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setState(prevState => ({ ...prevState, [name]: value, userNameError: "", ageError: "" }));
    }

    const handleSubmit = () => {
        const { userName, age } = state;
        if (!userName) {
            setState(prevState => ({ ...prevState, userNameError: "Please Enter User Name" }));
            return;
        } else if (!age) {
            setState(prevState => ({ ...prevState, ageError: "Please Enter Age" }));
            return;
        }
        let newUser = {
            "userName": userName,
            "age": age
        }
        setState(prevState => ({ ...prevState, UserData: [...prevState.UserData, newUser], userName: "", age: "" }));
    }

    return (
        <>
            {
                show && (
                    <>
                        <Modal show={show} onHide={handleClose} dialogClassName="modal-right" size="md">
                            <Modal.Header className="modal__Header">
                                <Modal.Title id="example-modal-sizes-title-lg">Edit</Modal.Title>
                                <div onClick={handleClose} className="close__btn">X</div>
                            </Modal.Header>
                            <Modal.Body>
                                <div class="row mt-2">
                                    <div class="col-lg-12">
                                        <div className="row col-lg-12">
                                            <div>
                                                <h6>User Name : <span>{state.userNameError}</span></h6>
                                                <input type="text" name="userName" value={state.userName} className="form-control" onChange={handleChange} placeholder="Enter User Name" />
                                            </div>
                                            <div className="mt-2">
                                                <h6>Age : <span>{state.ageError}</span></h6>
                                                <input type="number" name="age" value={state.age} className="form-control" onChange={handleChange} placeholder="Enter Age" />
                                            </div>
                                            <div>
                                                <button className="btn btn-success mt-3" onClick={handleSubmit}>Submit</button>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </Modal.Body>
                        </Modal>
                    </>
                )
            }
        </>
    )
}
export default CurdEditChanges;
