import React, { useState } from "react";

const arrayData = [
    { id: 1, name: "Naresh", isChecked: false },
    { id: 2, name: "Raresh", isChecked: false },
    { id: 3, name: "Suresh", isChecked: false },
];

const Example2 = () => {
    const [state, setState] = useState({
        arrayDetails: arrayData,
    });

    const handleInputChange = (id) => {
        const updatedArrayDetails = state.arrayDetails.map((item) =>
            item.id === id ? { ...item, isChecked: !item.isChecked } : item
        );
        setState({
            arrayDetails: updatedArrayDetails,
            isCheckedData: updatedArrayDetails.some((item) => item.isChecked),
        });
    };

    const checkType = [{ type: "checkbox", label: "Checked", name: "isChecked" }];

    const handleDelete = (index) => {
        setState((prevState) => ({
            arrayDetails: prevState.arrayDetails.filter((_, i) => i !== index),
            isCheckedData: prevState.arrayDetails.filter((_, i) => i !== index).some((item) => item.isChecked),
        }));
    };

    return (
        <>
            <ul>
                {state.arrayDetails.map((item, index) => (
                    <li key={item.id} className="h4">
                        {checkType.map((checkbox, idx) => (
                            <input
                                key={idx}
                                type={checkbox.type}
                                name={checkbox.name}
                                checked={item.isChecked}
                                onChange={() => handleInputChange(item.id)}
                            />
                        ))}{" "}
                        {index + 1} - {item.name}
                        {state.isCheckedData && (
                            <button onClick={() => handleDelete(index)}>Delete</button>
                        )}
                    </li>
                ))}
            </ul>
        </>
    );
};

export default Example2;
